targets.expr:
label_replace(
sum by(id) (
    sum without(cpu) (
        rate(container_cpu_usage_seconds_total{instance="$instance", id=~"$id", cpu="total"}[$__rate_interval])
    )
    /
    (
      container_spec_cpu_quota{instance="$instance", id=~"$id"}
      /
      container_spec_cpu_period{instance="$instance", id=~"$id"}
    )
)
, "id", "$1", "id", "$id_prefix/?(.*)"
)

label_replace(
max by(id) (
  max_over_time(container_memory_rss{instance="$instance", id=~"$id"}[$__interval])
  or
  container_memory_rss{instance="$instance", id=~"$id"}
)
, "id", "$1", "id", "$id_prefix/?(.*)"
)

label_replace(
max by(id) (
  max_over_time(container_memory_working_set_bytes{instance="$instance", id=~"$id"}[$__interval])
  or
  container_memory_working_set_bytes{instance="$instance", id=~"$id"}
)
, "id", "$1", "id", "$id_prefix/?(.*)"
)

label_replace(
max by(id) (
  (
    max_over_time(container_memory_rss{instance="$instance", id=~"$id"}[$__interval])
    or
    container_memory_rss{instance="$instance", id=~"$id"}
  )
/
  (max_over_time(container_spec_memory_limit_bytes{instance="$instance", id=~"$id"}[$__interval]) != 0)
)
, "id", "$1", "id", "$id_prefix/?(.*)"
)

label_replace(
