targets.expr:
count by(instance, container_label_com_docker_compose_project) (
  max by(instance, container_label_com_docker_compose_project, name) (max_over_time(container_start_time_seconds{instance=~"$instance", name=~"$name"}[$__range]))
)

sum by(instance, container_label_com_docker_compose_project) (
    sum without(cpu) (
        rate(container_cpu_usage_seconds_total{instance=~"$instance", name=~"$name", cpu="total"}[$__rate_interval])
    )
    /
    (
      container_spec_cpu_quota{instance=~"$instance", name=~"$name"}
      /
      container_spec_cpu_period{instance=~"$instance", name=~"$name"}
    )
)

sum by(instance, container_label_com_docker_compose_project) (
  max by(instance, container_label_com_docker_compose_project, name) (
    max_over_time(container_memory_rss{instance=~"$instance", name=~"$name"}[$__interval])
    or
    container_memory_rss{instance=~"$instance", name=~"$name"}
  )
)

sum(
  max by(instance, container_label_com_docker_compose_project, name) (
    max_over_time(container_memory_rss{instance=~"$instance", name=~"$name", invalid=$show_total}[$__interval])
    or
    container_memory_rss{instance=~"$instance", name=~"$name", invalid=$show_total}
  )
)

sum by(instance, container_label_com_docker_compose_project) (
  max by(instance, container_label_com_docker_compose_project, name) (
    max_over_time(container_memory_working_set_bytes{instance=~"$instance", name=~"$name"}[$__interval])
    or
    container_memory_working_set_bytes{instance=~"$instance", name=~"$name"}
  )
)

sum(
  max by(instance, container_label_com_docker_compose_project, name) (
    max_over_time(container_memory_working_set_bytes{instance=~"$instance", name=~"$name", invalid=$show_total}[$__interval])
    or
    container_memory_working_set_bytes{instance=~"$instance", name=~"$name", invalid=$show_total}
  )
)

max by(instance, container_label_com_docker_compose_project) (
    (
        max_over_time(container_memory_rss{instance=~"$instance", name=~"$name"}[$__interval])
        or
        container_memory_rss{instance=~"$instance", name=~"$name"}
    )
/
    (max_over_time(container_spec_memory_limit_bytes{instance=~"$instance", name=~"$name"}[$__interval]) != 0)
)

max by(instance, container_label_com_docker_compose_project) (
    (
        max_over_time(container_memory_working_set_bytes{instance=~"$instance", name=~"$name"}[$__interval])
        or
        container_memory_working_set_bytes{instance=~"$instance", name=~"$name"}
    )
/
    (max_over_time(container_spec_memory_limit_bytes{instance=~"$instance", name=~"$name"}[$__interval]) != 0)
)
