targets.expr:
max by(id, name, container_label_com_docker_compose_project, container_label_com_docker_compose_service, image) (
  max_over_time(container_start_time_seconds{instance="$instance", name=~"$name", id=~"$id"}[$__range])
)

max by(id, name) (
  max without(uid) (max_over_time(container_last_seen{instance="$instance", name=~"$name", id=~"$id"}[$__range]))
) * 1000

sum by($split_ids) (
  container_spec_cpu_quota{instance="$instance", name=~"$name", id=~"$id"}
  /
  container_spec_cpu_period{instance="$instance", name=~"$name", id=~"$id"}
)

sum by($split_ids) (
    sum without(cpu) (
        rate(container_cpu_usage_seconds_total{instance="$instance", name=~"$name", id=~"$id", cpu="total"}[$__rate_interval])
    )
    /
    (
      container_spec_cpu_quota{instance="$instance", name=~"$name", id=~"$id"}
      /
      container_spec_cpu_period{instance="$instance", name=~"$name", id=~"$id"}
    )
)
