targets.expr:
max by(pod, container, name, instance) (
  max_over_time(container_start_time_seconds{location="$location", namespace="$namespace", pod=~"$pod", container=~"$container", name=~"$id"}[$__range])
) * 1000

max by($split_ids) (
  container_spec_cpu_quota{location="$location", namespace="$namespace", pod="$pod", container=~"$container", name=~"$id"}
  /
  container_spec_cpu_period{location="$location", namespace="$namespace", pod="$pod", container=~"$container", name=~"$id"}
)

sum by(container) (
    kube_pod_container_cgroup_burst{location="$location", namespace="$namespace", pod="$pod", container=~"$container"}
)

sum by($split_ids) (
  (rate(container_cpu_cfs_throttled_seconds_total{location="$location", namespace="$namespace", pod="$pod", container=~"$container", name=~"$id"}[$__rate_interval]) * $__interval_ms / 1000 )
)
